SELECT cloud_account.id AS cloudAccountId,
cloud_account.`name` AS cloudAccountName,
plugin.icon AS icon,
plugin.description AS plugin
FROM cloud_account
LEFT JOIN plugin ON cloud_account.plugin_name = plugin.name
WHERE `plugin`.plugin_type='container'

    AND cloud_account.id = #{accountId}


    AND cloud_account.id IN (SELECT cs_project.account_id FROM cs_project WHERE cs_project.workspace_id IN

        #{item}

    )


    AND cloud_account.id IN (SELECT cs_project.account_id FROM cs_project WHERE cs_project.workspace_id =
    #{workspaceId}
    )


    AND cloud_account.id IN (SELECT cs_project.account_id FROM cs_project WHERE cs_project.id IN

        #{item}

    )

SELECT cloud_account.id AS cloudAccountId,
cloud_account.`name` AS cloudAccountName,
plugin.icon AS icon,
plugin.description AS plugin,
(select count(0) FROM cs_node WHERE cs_node.account_id = cloud_account.id) AS node,
(select count(0) FROM cs_project WHERE cs_project.account_id = cloud_account.id

    and cs_project.id in

        #{item}


) AS project,
(select count(0) from cs_pod WHERE cs_pod.account_id = cloud_account.id

    and cs_pod.project_id in

        #{item}


) AS pod,
(select count(0) from cs_container WHERE cs_container.account_id = cloud_account.id

    and cs_container.project_id in

        #{item}


) AS container
FROM cloud_account
LEFT JOIN plugin ON cloud_account.plugin_name = plugin.name
WHERE `plugin`.plugin_type='container'

    AND cloud_account.id = #{accountId}
